        System.out.println("3 ?= " + longestSubstring("aaabb", 3));
        System.out.println("5 ?= " + longestSubstring("ababbc", 2));
        System.out.println("6 ?= " + longestSubstring("ababbc", 1));
        System.out.println("6 ?= " + longestSubstring("ababbc", 0));
